frm_get_stock_block_data <- function(dbfile){
	# get stock block infos
	# frm_get_stock_block_data()
	url<-"http://nufm.dfcfw.com/EM_Finance2014NumericApplication/JS.aspx?type=CT&cmd=C._BKHY&sty=FPGBKI&st=c&sr=-1&p=1&ps=5000&token=7bc05d0d4c3c22ef9fca8c2a912d779c&v=0.644708752505078"
	url_string <- getURL(url)
	url_string <- url_string %>% 
		stringr::str_replace_all("\\(","") %>% 
		stringr::str_replace_all("\\)","") %>%
		stringr::str_replace_all("\\]","") %>%
		stringr::str_replace_all("\\[","") %>%
		stringr::str_replace_all("\\|",",") 

	url_split <- url_string %>% stringr::str_split("\"") %>% `[[`(1) # %>% as.data.frame
	url_effect <- url_split[url_split != "" & url_split !=","]
	df_block <- url_effect %>% ldply(function(x) x %>% stringr::str_split(",") %>% unlist)
	colnames(df_block) <- c("order	remark",	"blockid",	"block_name",	"zhangfu",	
			"newprice",	"hsl",	"up1", "up2","down1","down2",
			"stock1_id","stock1_index","stock1_name","stock1_price","stock1_zdf",
			"stock2_id","stock2_index","stock2_name","stock2_price","stock2_zdf",
			"unknow1","unknow2","unknow3"
			)	
	# df_block$date <- format(Sys.time(), "%Y%m%d_%H_%M_%OS3") 
	df_block$date <-Sys.time()

	dbaction <- cl_db_oprate$new( db_file)
  	dbaction$frm_save_fin_table2_sqlite(db_file,df_block,tb_name="stock_block")

	DT::datatable(df_block)
}

