frm_add_other_jscode2hc <- function (hc, filename = NULL,add_hc_fun,hc_obj="Chart"){
	# add other js code to R highcharts object 
	#  hc_obj ="Chart" "stockChart"
    . <- NULL
    stopifnot(!is.null(filename))
    if (!str_detect(filename, ".js$"))
        filename <- str_c(filename, ".html")
    jslns <- hc$x$hc_opts %>% toJSON(pretty = TRUE, auto_unbox = TRUE,
        force = TRUE, null = "null") %>% str_split("\n") %>%
        head(1) %>% unlist() %>% str_replace("\"", "") %>% str_replace("\":",
        ":")
    fflag <- str_detect(jslns, "function()")
    if (any(fflag)) {
        jslns <- ifelse(fflag, str_replace(jslns, "\"function",
            "function"), jslns)
        jslns <- ifelse(fflag, str_replace(jslns, "\",$", ","),
            jslns)
        jslns <- ifelse(fflag, str_replace(jslns, "\"$", ""),
            jslns)
        jslns <- ifelse(fflag, str_replace_all(jslns, "\\\\n",
            str_c("\\\\n", str_extract(jslns, "^\\s+"))), jslns)
    }
    jslns <- jslns %>% unlist() %>% tail(-1) %>% str_c("    ",
        ., collapse = "\n") %>% str_replace_all("\n\\s{4,}\\]\\,\n\\s{4,}\\[\n\\s{4,}",
        "],[") %>% 
        sprintf(" \nvar chart_syrhades;\n 
        			$(document).ready(function () {\n
        			   chart_syrhades = new Highcharts.%s({\n%s\n  );\n
						%s
        					});",
       hc_obj,.,add_hc_fun)
     jslns
}


frm_gen_hc_html <- function(plot_hc_script,filename) {
	# generate plot _hc script & save the file to html file for Highcharts.chart
	tag_script_jquery <- tag("script", list(type="text/javascript",src="http://ajax.microsoft.com/ajax/jquery
/jquery-1.4.min.js"))
	tag_script_hightcharts <- tag("script", list(type="text/javascript",src="https://code.highcharts.com/highcharts.js"))
	
	tag_script_highstock <- tag("script", list(type="text/javascript",src="http://cdn.hcharts.cn/highstock/highstock.js"))
	tag_script_exporting <- tag("script", list(type="text/javascript",src="http://cdn.hcharts.cn/highstock/modules/exporting.js"))

	tag_plot <- div(id='container', class='simpleDiv',
	       style="height: 400px")
	tag_script_plot_option <- tag("script", list(type="text/javascript",plot_hc_script ))
	# tag_hc_add_rect <- tag("script", list(type="text/javascript",script_str))
	doc <- tags$html(
	    tag_script_jquery,
	    tag_script_hightcharts,
	    tag_script_highstock,
	    tag_script_exporting,
		tags$body(
		  tag_script_plot_option,
		  # tag_hc_add_rect,
		  tag_plot
	  )
	)
	# cat(as.character(doc))
	doc%>% as.character %>% writeLines(filename)
}

frm_rewrite_highchart_html <- function(hc,filename){
	hc$x$hc_opts$chart <- list(renderTo = 'container') # set hc_opts chart attr: renderTo
	function(add_jscode){
		function(FUN1=frm_add_other_jscode2hc,...){
			# generate hc js code add some function
			hc_script <- hc %>% FUN1("e:/testexporthc",add_jscode,...)
			# generate html of highchart 
			function(FUN_gen_html=frm_gen_hc_html){
				hc_script %>% FUN_gen_html(filename)
			}
		}
	}
}

